  var Maps = new HMap.Map();
  Maps.initMap('map', {
    view: {
      center: [0, 0], // 视图中心点（必须）
      enableRotation: true, // 是否允许旋转
      projection: 'EPSG:3857', // 视图投影坐标系（不传时默认3857）
      rotation: 0, // 视图旋转角（不传默认为0）
      zoom: 0, // resolution
      zoomFactor: 2 // 用于约束分变率的缩放因子（高分辨率设备需要注意）
    },
    logo: {},
